// import {useState,useEffect} from 'react';
import {Component} from 'react';
import CardList from './component/card-list/cardlist.component';
import SearchBox from './component/search-box/search-box.component';
import './App.css';


class App extends Component {
  constructor() {
    super();

    this.state = {
     monsters:[],
     serachField: '',    

    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) =>  response.json())
    .then((user) => this.setState(() => {
      return {monsters:user}
    },
    // () => console.log(this.state)
  ))   


  }

  onSearchChange = (event) => {      

    const serachField = event.target.value.toLocaleLowerCase();    

    this.setState(() => {
      return {serachField}
    })
  }

  render(){
     const {monsters,serachField} = this.state;
     const {onSearchChange} = this;

    const filteredMonster = monsters.filter((monster) =>{
      return monster.name.toLocaleLowerCase().includes(serachField);
    });

  return (
    <div className='App' >
    <SearchBox className='monsters-search-box' onChangeHandler={onSearchChange} placeholder='search monsters'/>
    <CardList monsters={filteredMonster}/>

    </div>

  )
};
}

export default App;
